// Login.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './login.css';

const Login = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');

  const navigate = useNavigate();

  const handleLogin = () => {
    // Add your authentication logic here
    // For simplicity, let's assume the login is successful if the username and password are not empty

    if (username && password) {
      // Redirect to the join page upon successful login
      navigate('/join');
    } else {
      setError('Invalid credentials');
    }
  };

  return (
    <div className="center-align">
      <div className="login-container">
        <h2>Login</h2>
        <div className="login-form-group">
          <label htmlFor="username" className="login-label">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="login-input"
          />
        </div>
        <div className="login-form-group">
          <label htmlFor="password" className="login-label">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="login-input"
          />
        </div>
        {error && <div className="login-error-message">{error}</div>}
        <button onClick={handleLogin} className="login-button">Login</button>
        <p className="login-p">
          Don't have an account? <Link to="/register" className="login-link">Register</Link>
        </p>
      </div>
    </div>
  );
};

export default Login;
