import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { useLocation } from 'react-router-dom';

function Chat() {
    const [name, setName] = useState('');
    const [room, setRoom] = useState('');
    const [socket, setSocket] = useState(null); // Create a state for the socket
    const location = useLocation();

    useEffect(() => {
        const data = new URLSearchParams(location.search);
        const newName = data.get('name');
        const newRoom = data.get('room');

        if (newName && newRoom) {
            setName(newName);
            setRoom(newRoom);
            const newSocket = io('http://localhost:5000');
            setSocket(newSocket); // Set the socket state
            console.log(newName, newRoom);
            console.log(newSocket);
        } else {
            console.error("Name and room not found in URL parameters");
        }
    }, [location.search]);

    return <h1>hello world</h1>;
}

export default Chat;
