import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { useLocation } from 'react-router-dom'; // Import useLocation from react-router-dom
function Chat(){
  const [name, setName] = useState('');
    const [room, setRoom] = useState('');
    const [message,setMessage]=useState('');
    const [messages,setMessages]=useState([]);
  const location=useLocation();
  useEffect(()=>{
    const data = new URLSearchParams(location.search);
    const name = data.get('name');
    const room = data.get('room');
console.log(name);
console.log(room);
//const socket=io('localhost:5000');
const socket = io('http://localhost:5000');
  console.log(socket);
setName(name);
    setRoom(room);
    socket.emit('join',{ name,room });
    /*
    socket.emit('join',{ name,room },({error})=>{
    alert(error);
    });
    */
   //this is the termination of first useeffect hook
   return ()=>{
    socket.emit('disconnect');//name same as that is present in backend.(see index.js in server folder)
    socket.off();
   }
  },[location.search])
  useEffect(()=>{
    const socket = io('http://localhost:5000');
    socket.on('message',(message)=>{
      setMessage([...messages,message]);
      console.log(message);
    })
  },[messages]);
  const socket = io('http://localhost:5000');
  const sendMessage=(event)=>{
    event.preventDefault();//autometically refreshes the page
    if(messages){
      socket.emit('sendMessage',messages,()=>setMessage(''));
    }
  }
  console.log(messages,message);
 return (
  <div className="outerContainer">
  <div className="innerContainer">
  <input value={message}
   onChange={(event)=>setMessage(event.target.value)}
   onKeyPress={(event)=>event.key==='Enter'?sendMessage(event):null}
   />
  </div>
  </div>
 );
}
export default Chat;
