// App.js
import React from 'react';
import Join from './components/join/join';
import Chat from './components/chat/chat';
import Login from './components/login/login';
import { Routes, Route, Navigate } from 'react-router-dom';

function App() {
  return (
    <Routes>
      <Route path="/" element={<oin />} />
      <Route path="/join" element={<Login />} />
      <Route
        path="/chat"
        element={
          // Example of conditional navigation based on some condition
          // This should be done within a component, not directly in the route configuration
          localStorage.getItem('user') ? <Chat /> : <Navigate to="/" />
        }
      />
    </Routes>
  );
}

export default App;
