import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import { useLocation } from 'react-router-dom';

function Chat() {
  const [name, setName] = useState('');
  const [room, setRoom] = useState('');
  const [message, setMessage] = useState('');
  const [messages, setMessages] = useState([]);
  const location = useLocation();

  useEffect(() => {
    const data = new URLSearchParams(location.search);
    const name = data.get('name');
    const room = data.get('room');
    console.log(name);
    console.log(room);

    const socket = io('http://localhost:5000');
    setName(name);
    setRoom(room);

    socket.emit('join', { name, room });

    return () => {
      socket.disconnect(); // Use 'disconnect' to disconnect the socket
    };
  }, [location.search]);

  useEffect(() => {
    const socket = io('http://localhost:5000');

    // Use a callback function to update state based on previous state
    socket.on('message', (message) => {
      setMessages(prevMessages => [...prevMessages, message]);
      console.log(message);
    });

    // Cleanup the socket event listener when component unmounts
    return () => {
      socket.disconnect();
      socket.off('message');
    };
  }, []); // Add an empty dependency array to run this effect only once

  const socket = io('http://localhost:5000'); // You don't need this here, it's already defined in the previous useEffect

  const sendMessage = (event) => {
    event.preventDefault();
    if (message) {
      socket.emit('sendMessage', message, () => setMessage(''));
    }
  };

  console.log(message, messages);

  return (
    <div className="outerContainer">
      <div className="innerContainer">
        <input
          value={message}
          onChange={(event) => setMessage(event.target.value)}
          onKeyPress={(event) => event.key === 'Enter' ? sendMessage(event) : null}
        />
      </div>
    </div>
  );
}

export default Chat;
