// Register.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './register.css';

const Register = () => {
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const [confirmPassword, setConfirmPassword] = useState('');
  const [error, setError] = useState('');

  const navigate = useNavigate();

  const handleRegister = () => {
    // Add your registration logic here
    // For simplicity, let's assume registration is successful if all fields are filled and passwords match

    if (username && password && confirmPassword && password === confirmPassword) {
      // Redirect to the join page upon successful registration
      navigate('/join');
    } else {
      setError('Invalid registration details');
    }
  };

  return (
    <div className="center-align">
      <div className="register-container">
        <h2>Register</h2>
        <div className="register-form-group">
          <label htmlFor="username" className="register-label">Username:</label>
          <input
            type="text"
            id="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            className="register-input"
          />
        </div>
        <div className="register-form-group">
          <label htmlFor="password" className="register-label">Password:</label>
          <input
            type="password"
            id="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="register-input"
          />
        </div>
        <div className="register-form-group">
          <label htmlFor="confirmPassword" className="register-label">Confirm Password:</label>
          <input
            type="password"
            id="confirmPassword"
            value={confirmPassword}
            onChange={(e) => setConfirmPassword(e.target.value)}
            className="register-input"
          />
        </div>
        {error && <div className="register-error-message">{error}</div>}
        <button onClick={handleRegister} className="register-button">Register</button>
        <p className="register-p">
          Already have an account? <Link to="/" className="register-link">Login now</Link>
        </p>
      </div>
    </div>
  );
};

export default Register;
